import Currency from "./3-currency";

export default class Pricing {
  constructor(amount, currency) {
    if (isNaN(amount) || !(currency instanceof Currency)) {
      throw new Error("put in valid options");
    }
    this._amount = amount;
    this._currency = currency;
  }

  get amount() {
    return this._amount;
  }
  set amount(money) {
    if (isNaN(amount)) throw new Error("Put a number");
    this._amount = money;
  }
  set currency(current) {
    if (!(currency instanceof Currency)) {
      throw new Error("Put an object");
    }
    this._currency = current;
  }
  get currency() {
    return this._currency;
  }
  displayFullPrice() {
    return `${this._amount} ${this._currency._name} (${this._currency._code})`;
  }
  static convertPrice(amount, conversionRate) {
    if (isNaN(amount) || isNaN(conversionRate)) {
      throw new Error();
    }
    return amount * conversionRate;
  }
}
